import React, { useState, useEffect, useRef } from 'react';
import { Play, Pause, Square, RotateCcw, Clock } from 'lucide-react';

const StopwatchApp = () => {
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [laps, setLaps] = useState([]);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(() => {
        setTime(prevTime => prevTime + 10);
      }, 10);
    } else {
      clearInterval(intervalRef.current);
    }

    return () => clearInterval(intervalRef.current);
  }, [isRunning]);

  const formatTime = (milliseconds) => {
    const totalSeconds = Math.floor(milliseconds / 1000);
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    const ms = Math.floor((milliseconds % 1000) / 10);

    return `${minutes.toString().padStart(2, '0')}:${seconds
      .toString()
      .padStart(2, '0')}.${ms.toString().padStart(2, '0')}`;
  };

  const handleStart = () => {
    setIsRunning(true);
  };

  const handlePause = () => {
    setIsRunning(false);
  };

  const handleReset = () => {
    setIsRunning(false);
    setTime(0);
    setLaps([]);
  };

  const handleLap = () => {
    if (isRunning && time > 0) {
      const lapTime = time;
      const lapNumber = laps.length + 1;
      const previousLapTime = laps.length > 0 ? laps[laps.length - 1].totalTime : 0;
      const splitTime = lapTime - previousLapTime;

      setLaps(prevLaps => [
        ...prevLaps,
        {
          number: lapNumber,
          totalTime: lapTime,
          splitTime: splitTime
        }
      ]);
    }
  };

  const getFastestLap = () => {
    if (laps.length === 0) return null;
    return laps.reduce((fastest, current) => 
      current.splitTime < fastest.splitTime ? current : fastest
    );
  };

  const getSlowestLap = () => {
    if (laps.length === 0) return null;
    return laps.reduce((slowest, current) => 
      current.splitTime > slowest.splitTime ? current : slowest
    );
  };

  const fastestLap = getFastestLap();
  const slowestLap = getSlowestLap();

  return (
<div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-800 p-6 flex items-center justify-center">

      <div className="max-w-md mx-auto">
        {/* Header */}
        <div className="text-center mb-8 pt-8">
          <div className="flex items-center justify-center mb-4">
            <Clock className="text-white text-3xl mr-3" />
            <h1 className="text-3xl font-bold text-white">Stopwatch Pro</h1>
          </div>
          <p className="text-gray-300">Professional Time Tracking</p>
        </div>

        {/* Main Stopwatch Display */}
        <div className="bg-white/10 backdrop-blur-lg rounded-3xl p-8 mb-6 border border-white/20">
          <div className="text-center">
            <div className="text-6xl font-mono font-bold text-white mb-8 tracking-wider">
              {formatTime(time)}
            </div>
            
            {/* Control Buttons */}
            <div className="flex justify-center gap-4 mb-6">
              {!isRunning ? (
                <button
                  onClick={handleStart}
                  className="bg-green-500 hover:bg-green-600 text-white px-8 py-4 rounded-full font-semibold transition-all duration-200 flex items-center gap-2 shadow-lg hover:shadow-xl transform hover:scale-105"
                >
                  <Play size={20} />
                  Start
                </button>
              ) : (
                <button
                  onClick={handlePause}
                  className="bg-yellow-500 hover:bg-yellow-600 text-white px-8 py-4 rounded-full font-semibold transition-all duration-200 flex items-center gap-2 shadow-lg hover:shadow-xl transform hover:scale-105"
                >
                  <Pause size={20} />
                  Pause
                </button>
              )}
              
              <button
                onClick={handleReset}
                className="bg-red-500 hover:bg-red-600 text-white px-8 py-4 rounded-full font-semibold transition-all duration-200 flex items-center gap-2 shadow-lg hover:shadow-xl transform hover:scale-105"
              >
                <RotateCcw size={20} />
                Reset
              </button>
            </div>

            {/* Lap Button */}
            <button
              onClick={handleLap}
              disabled={!isRunning || time === 0}
              className="bg-blue-500 hover:bg-blue-600 disabled:bg-gray-500 disabled:cursor-not-allowed text-white px-12 py-3 rounded-full font-semibold transition-all duration-200 shadow-lg hover:shadow-xl transform hover:scale-105 disabled:transform-none"
            >
              <Square size={18} className="inline mr-2" />
              Lap
            </button>
          </div>
        </div>

        {/* Lap Times Display */}
        {laps.length > 0 && (
          <div className="bg-white/10 backdrop-blur-lg rounded-3xl p-6 border border-white/20">
            <h2 className="text-xl font-bold text-white mb-4 text-center">
              Lap Times ({laps.length})
            </h2>
            
            <div className="max-h-64 overflow-y-auto space-y-2">
              {laps.slice().reverse().map((lap) => (
                <div
                  key={lap.number}
                  className={`flex justify-between items-center p-3 rounded-lg transition-all ${
                    fastestLap && lap.number === fastestLap.number
                      ? 'bg-green-500/20 border border-green-500/40'
                      : slowestLap && lap.number === slowestLap.number
                      ? 'bg-red-500/20 border border-red-500/40'
                      : 'bg-white/5 hover:bg-white/10'
                  }`}
                >
                  <div className="flex items-center gap-4">
                    <span className="text-gray-300 font-semibold w-12">
                      #{lap.number}
                    </span>
                    <div className="text-white">
                      <div className="font-mono font-bold">
                        {formatTime(lap.splitTime)}
                      </div>
                      <div className="text-xs text-gray-400">
                        Total: {formatTime(lap.totalTime)}
                      </div>
                    </div>
                  </div>
                  
                  <div className="text-right">
                    {fastestLap && lap.number === fastestLap.number && (
                      <span className="text-green-400 text-xs font-semibold">
                        FASTEST
                      </span>
                    )}
                    {slowestLap && lap.number === slowestLap.number && laps.length > 1 && (
                      <span className="text-red-400 text-xs font-semibold">
                        SLOWEST
                      </span>
                    )}
                  </div>
                </div>
              ))}
            </div>
            
            {/* Statistics */}
            {laps.length > 1 && (
              <div className="mt-4 pt-4 border-t border-white/20">
                <div className="grid grid-cols-2 gap-4 text-center">
                  <div className="bg-green-500/10 rounded-lg p-3">
                    <div className="text-green-400 text-xs font-semibold mb-1">
                      BEST LAP
                    </div>
                    <div className="text-white font-mono font-bold">
                      {formatTime(fastestLap.splitTime)}
                    </div>
                  </div>
                  <div className="bg-red-500/10 rounded-lg p-3">
                    <div className="text-red-400 text-xs font-semibold mb-1">
                      WORST LAP
                    </div>
                    <div className="text-white font-mono font-bold">
                      {formatTime(slowestLap.splitTime)}
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

        {/* Footer */}
        <div className="text-center mt-8 text-gray-400 text-sm">
          {/* <p>Built with React & Tailwind CSS</p> */}
          <p className="mt-1">Precision timing down to centiseconds</p>
        </div>
      </div>
    </div>
  );
};

export default StopwatchApp;
