import React, { useState, useEffect, useRef } from 'react';
import { Play, Pause, Square, RotateCcw, Clock } from 'lucide-react';

const StopwatchApp = () => {
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [laps, setLaps] = useState([]);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (isRunning) {
      intervalRef.current = setInterval(() => setTime(prev => prev + 10), 10);
    } else {
      clearInterval(intervalRef.current);
    }
    return () => clearInterval(intervalRef.current);
  }, [isRunning]);

  const formatTime = (ms) => {
    const totalSeconds = Math.floor(ms / 1000);
    const minutes = String(Math.floor(totalSeconds / 60));
    const seconds = String(totalSeconds % 60).padStart(2, '0');
    const centis = String(Math.floor((ms % 1000) / 10)).padStart(2, '0');
    return `${minutes}:${seconds}.${centis}`;
  };

  const handleStart = () => setIsRunning(true);
  const handlePause = () => setIsRunning(false);
  const handleReset = () => {
    setIsRunning(false);
    setTime(0);
    setLaps([]);
  };
  const handleLap = () => {
    if (isRunning && time > 0) {
      const previousTotal = laps.length > 0 ? laps[laps.length - 1].totalTime : 0;
      setLaps(prev => [
        ...prev,
        {
          number: prev.length + 1,
          totalTime: time,
          splitTime: time - previousTotal
        }
      ]);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-slate-900 via-slate-900 to-slate-800 p-6">
      <div className="w-full max-w-xl">
        {/* Header */}
        <div className="flex items-center justify-center gap-3 mb-6">
          <Clock className="text-slate-300" />
          <h1 className="text-slate-100 text-lg font-medium tracking-wide">Stopwatch</h1>
        </div>

        {/* Timer Display */}
        <div className="bg-white/6 backdrop-blur-sm border border-white/10 rounded-3xl p-6 shadow-2xl flex flex-col items-center">
          <div className="font-mono text-5xl text-white font-extralight tracking-wider mb-2">{formatTime(time)}</div>
          <div className="text-slate-400 text-sm mb-6">Lap & Timer</div>

          {/* Controls */}
          <div className="flex items-center justify-between w-full">
            <button
              onClick={handleReset}
              className="flex-1 mr-3 px-4 py-2 rounded-full bg-white/5 border border-white/8 text-sm text-slate-200 hover:bg-white/8 transition flex items-center justify-center gap-2"
            >
              <RotateCcw size={16} /> Reset
            </button>

            {!isRunning ? (
              <button
                onClick={handleStart}
                className="flex-none px-8 py-3 rounded-full bg-emerald-500 hover:bg-emerald-600 text-white font-semibold shadow transition transform active:scale-95"
              >
                <div className="flex items-center gap-3"><Play size={18} /> Start</div>
              </button>
            ) : (
              <button
                onClick={handlePause}
                className="flex-none px-8 py-3 rounded-full bg-yellow-500 hover:bg-yellow-600 text-white font-semibold shadow transition transform active:scale-95"
              >
                <div className="flex items-center gap-3"><Pause size={18} /> Pause</div>
              </button>
            )}

            <button
              onClick={handleLap}
              disabled={!isRunning || time === 0}
              className="flex-1 ml-3 px-4 py-2 rounded-full bg-white/5 border border-white/8 text-sm text-slate-200 disabled:opacity-50 hover:bg-white/8 transition flex items-center justify-center gap-2"
            >
              <Square size={16} /> Lap
            </button>
          </div>
        </div>

        {/* Lap List */}
        <div className="mt-6 bg-white/4 backdrop-blur-sm border border-white/8 rounded-2xl p-4 max-h-72 overflow-y-auto">
          <div className="flex items-center justify-between mb-3 px-1">
            <h2 className="text-slate-200 font-medium">Laps</h2>
            <span className="text-slate-400 text-sm">{laps.length}</span>
          </div>

          {laps.length === 0 ? (
            <div className="py-8 text-center text-slate-500">No laps yet â€” press Lap while timer is running</div>
          ) : (
            <ul className="divide-y divide-white/8">
              {laps.slice().reverse().map(lap => (
                <li key={lap.number} className="flex items-center justify-between py-3">
                  <div className="flex items-center gap-3">
                    <div className="w-10 text-slate-300 font-semibold">#{lap.number}</div>
                    <div className="flex flex-col">
                      <span className="text-white font-mono font-semibold">{formatTime(lap.splitTime)}</span>
                      <span className="text-slate-400 text-xs">Total {formatTime(lap.totalTime)}</span>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default StopwatchApp;
